import React, { Component } from 'react';
import { Menu, Popup, Input } from 'semantic-ui-react';
import CartComponent from '../containers/CartComponent';


 const TopMenu = (props) => {
  
    const {itemsCart, quantityInCart, filterBy} = props;
    console.log(filterBy)
    return (
      <Menu>
        <Menu.Item name='browse' onClick={(name)=>filterBy(name)} >
          Browse
        </Menu.Item>

        <Menu.Item name='submit'  >
          Submit
        </Menu.Item>
        <Input placeholder='Search...' />
        <Menu.Menu position='right'>
            <Popup
                size='mini'
                trigger={
                <Menu.Item name="cart" >
                    Корзина (<b>{quantityInCart}</b>)
                </Menu.Item>
                }
                content =  {itemsCart.length >0 && itemsCart.map((itemCart, key) => (<CartComponent key={key} {...itemCart} />)) || 'В корзине нет товаров' }
                
                
                on='click'
            />
        </Menu.Menu>
      </Menu>
    )
}
export default TopMenu
