import react from 'react';
import App from '../components/App'




const mapStateToProps = ({products}) => ({
    products,
    isReady: products.isReady,
  })
  
  const mapDispatchToProps = (dispatch) => ({
    setProducts: (obj) => 
      dispatch({
        type: 'SET_PRODUCTS',
        payload: obj,
      })
  })
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  ) (App);
