const initialState = {
    itemsCart: [
        {
            "id": 0,
            "image": "https://podarok-v-korobke.ru/images/thumbnails/400/350/detailed/1/для_путешественника.jpg",
            "title": "Для путешественника",
            "price": 1424,
            "stars": 0
           },
           {
            "id": 1,
            "image": "https://podarok-v-korobke.ru/images/thumbnails/400/350/detailed/1/для_самой_потрясающей.jpg",
            "title": "Для самой потрясающей",
            "price": 914,
            "stars": 0
           },
    ]
}

export default (state = initialState, action) => {
    switch(action.type) {
        case 'ADD_TO_CART':
            return {
                ...state,
                itemsCart: [...state.itemsCart, action.payload]
            }
         case 'REMOVE_FROM_CART':
            return {
                ...state,
                itemsCart: state.itemsCart.filter(itemCart => itemCart.id != action.payload)
            }
            break;
        default:
            return state;
    }
}
