import React, { Component } from 'react';
import { Menu, Popup } from 'semantic-ui-react';
import CartComponent from '../containers/CartComponent';


export default class MenuExampleMenus extends Component {
  state = {}

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })
  
  render() {
    const { activeItem } = this.state;
    const {itemsCart, quantityInCart} = this.props;
    console.log(itemsCart)
    return (
      <Menu>
        <Menu.Item name='browse' active={activeItem === 'browse'} onClick={this.handleItemClick}>
          Browse
        </Menu.Item>

        <Menu.Item name='submit' active={activeItem === 'submit'} onClick={this.handleItemClick}>
          Submit
        </Menu.Item>

        <Menu.Menu position='right'>
            <Popup
                size='mini'
                trigger={
                <Menu.Item name="cart" >
                    Корзина (<b>{quantityInCart}</b>)
                </Menu.Item>
                }
                content =  {itemsCart.length >0 && itemsCart.map((itemCart, key) => (<CartComponent key={key} {...itemCart} />)) || 'В корзине нет товаров' }
                
                
                on='click'
            />
        </Menu.Menu>
      </Menu>
    )
  }
}
