import React from 'react';
import { Card, Image, Button } from 'semantic-ui-react'

const CartComponent = (props) => {

    const {removeFromCart, id, title, image} = props;
    return (
    
    <Card>
        <Card.Content>
        <Image floated='left' size='mini' src={image} />
        <Card.Header>{title}</Card.Header>(2 шт.)
        <Card.Meta>200 р.
        <Button size='mini' attached='left' color='red' floated='right'  onClick={removeFromCart.bind(this, id)}>x</Button>
        </Card.Meta>
        
        </Card.Content>
    </Card>
    )
}
export default CartComponent;
