const initialState = {
    items: [],
    isReady: false,
}

export default (state = initialState, action) => {
    switch(action.type) {
        case 'SET_PRODUCTS':
            return {
                isReady: true,
                ...state,
                items: action.payload,
                
            };
            break;

        default:
            return state;    
    }
}
