import react from 'react';
import App from '../components/App';
import { connect } from 'react-redux';
import orderBy from 'lodash/orderBy';

import setProductsAction from '../actions/products'

const filterProducts = (obj, filt) => {
  switch(filt) {
    case 'Дороже':
        return orderBy(obj, 'price', 'desc')        
    case 'Дешевле':
        return orderBy(obj, 'price', 'ask')
    case 'Популяпные':
        return orderBy(obj, 'stars', 'desc')
    default:
      return obj
  }

}

const mapStateToProps = ({products, filter}) => ({
    products: filterProducts(products.items, filter.filterBy),
    isReady: products.isReady,
  })
  
const mapDispatchToProps = (dispatch) => ({
  setProducts: (obj) => 
    dispatch(
      setProductsAction(obj)
    )
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
) (App);
