import React, { Component } from 'react';
import { Menu, Popup } from 'semantic-ui-react';
import CartComponent from '../containers/CartComponent';


export const MenuExampleMenus = () => {
  

    return (
      <Menu>
        <Menu.Item name='browse' active={activeItem === 'browse'}>
          Browse
        </Menu.Item>

        <Menu.Item name='submit' active={activeItem === 'submit'}>
          Submit
        </Menu.Item>

        <Menu.Menu position='right'>
            <Popup
                size='mini'
                trigger={
                <Menu.Item name="cart" >
                    Корзина (<b>30</b>)
                </Menu.Item>
                }
                content =  {itemsCart.map((itemCart, key) => (<CartComponent key={key} {...itemCart} />)  )  }
                
                on='click'
            />
        </Menu.Menu>
      </Menu>
    )
  
}
